let currentImageIndex=0;const galleryImages=[];function openLightbox(index){currentImageIndex=index;const lightbox=document.getElementById('lightbox');const lightboxImage=document.getElementById('lightbox-image');const lightboxCaption=document.getElementById('lightbox-caption');const imageCounter=document.getElementById('image-counter');lightboxImage.src=galleryImages[index].src;lightboxImage.alt=galleryImages[index].alt;imageCounter.textContent=`${index + 1} of ${galleryImages.length}`;if(galleryImages[index].caption){lightboxCaption.querySelector('p').textContent=galleryImages[index].caption;lightboxCaption.style.display='block';}else{lightboxCaption.style.display='none';}
lightbox.classList.remove('hidden');lightbox.classList.add('flex');document.body.style.overflow='hidden';}
function closeLightbox(){const lightbox=document.getElementById('lightbox');lightbox.classList.add('hidden');lightbox.classList.remove('flex');document.body.style.overflow='auto';}
function nextImage(){currentImageIndex=(currentImageIndex+1)%galleryImages.length;openLightbox(currentImageIndex);}
function previousImage(){currentImageIndex=(currentImageIndex-1+galleryImages.length)%galleryImages.length;openLightbox(currentImageIndex);}
document.addEventListener('keydown',function(e){if(e.key==='Escape'){closeLightbox();}else if(e.key==='ArrowRight'){nextImage();}else if(e.key==='ArrowLeft'){previousImage();}});document.getElementById('lightbox').addEventListener('click',function(e){if(e.target===this){closeLightbox();}});;