document.addEventListener('DOMContentLoaded',function(){const floatingButtons=document.getElementById('floating-buttons');const footer=document.querySelector('footer');if(footer&&floatingButtons){const observer=new IntersectionObserver((entries)=>{entries.forEach(entry=>{const footerRect=footer.getBoundingClientRect();const viewportHeight=window.innerHeight;if(entry.isIntersecting){const footerTop=footerRect.top;const visibleFooterHeight=viewportHeight-footerTop;const translateY=Math.max(0,visibleFooterHeight-64);floatingButtons.style.transform=`translateY(-${translateY}px)`;}else{floatingButtons.style.transform='translateY(0)';}});},{threshold:0,rootMargin:'0px'});observer.observe(footer);let ticking=false;function updateButtonPosition(){const footerRect=footer.getBoundingClientRect();const viewportHeight=window.innerHeight;if(footerRect.top<viewportHeight){const visibleFooterHeight=viewportHeight-footerRect.top;const translateY=Math.max(0,visibleFooterHeight-64);floatingButtons.style.transform=`translateY(-${translateY}px)`;}else{floatingButtons.style.transform='translateY(0)';}
ticking=false;}
function requestTick(){if(!ticking){requestAnimationFrame(updateButtonPosition);ticking=true;}}
window.addEventListener('scroll',requestTick);window.addEventListener('resize',requestTick);}});;