document.addEventListener('DOMContentLoaded',function(){const projectSections=document.querySelectorAll('#projects .min-h-screen');const projectImages=document.querySelectorAll('.project-image[data-project-index]');function updateActiveImage(){const scrollPosition=window.scrollY;projectSections.forEach((section,index)=>{const sectionTop=section.offsetTop;const sectionBottom=sectionTop+section.offsetHeight;if(scrollPosition>=sectionTop-window.innerHeight/2&&scrollPosition<sectionBottom-window.innerHeight/2){projectImages.forEach(img=>{img.classList.remove('opacity-100');img.classList.add('opacity-0');});const currentImage=document.querySelector(`.project-image[data-project-index="${index}"]`);if(currentImage){currentImage.classList.remove('opacity-0');currentImage.classList.add('opacity-100');}}});}
window.addEventListener('scroll',updateActiveImage);updateActiveImage();});;